mapper.select:
select id,organ_type organType, organ_id organId,organ_name organName,leader,address,address_phone addressPhone, organ_desc organDesc,phone,parent_id parentId,create_time createTime,update_time updateTime, update_user updateUser,create_user createUser,is_effect isEffect,record_state recordState, link_url linkUrl ,organ_icon organIcon from cms_system_organ where is_effect = 1 and record_state = 'Normal'
and id = #{id}
and organ_name like concat(concat('%',#{organName}),'%')
and organ_type = #{organType}
order by create_time asc
